th' => null,		'random' => false,		'order_post_by' => '',		'use_image' => '',		'use_layout' => 'default', //there can be 2 values. if we use "default" then views/post_layout_default.php will		//be used otherwise if we use "inline" - views/post_layout_inline.php will be used		'align_thumbnail' => 'left', //this attribute can be "left" or "right",		'wrap_excerpt_start' => '',		'wrap_excerpt_end' => '',		'show_after_date' => '',		'show_before_date' => '',		'show_for_today' => '',		'show_order' => false,        'show_filter_date' => false	);	private $attributes = array();	public function get_shortcode_attributes() {		return $this->attributes;	}	public function set_shortcode_attributes( $atts ) {		$this->attributes = shortcode_atts( Shortcode_Manager::$DEFAULT_VALUES, $atts );	}	public function add_attributes( $atts ) {		$this->attributes = array_merge( $this->attributes, $this->prepare_array( $atts ) );	}	public function has_value( $name ) {		return isset( $this->attributes[ $name ] ) && ! empty( $this->attributes[ $name ] );	}	public function get( $name ) {		return esc_attr( $this->attributes[ $name ] );	}	public function get_boolean( $name ) {		return $this->has_value( $name ) && strtolower( $this->get( $name ) ) == 'true';	}	public function is_match( $name, $pattern ) {		$has_value = $this->has_value( $name );		if ( $has_value ) {			$value = $this->get( $name );			return preg_match( $pattern, $value );		} else {			return false;		}	}	public function split_array( $name, $pattern ) {		$value = $this->get( $name );		return mb_split( $pattern, $value );	}	public static function newInstance( $atts ) {		$mgr = new Shortcode_Manager();		$mgr->set_shortcode_attributes( $atts );		return $mgr;	}	private function prepare_array( $data ) {		$new_data = [];		foreach ( $data as $key => $value ) {			$new_data[ $key ] = esc_sql( $value );		}		return $new_data;	}}